    /**
     *
     * @see android.gameengine.icadroids.engine.GameEngine#update()
     */
    @Override
    public void update() {
        super.update();
        if(isGameTimeUp()){
            runGameOverDisplay(); //ToDo: game over display?
            Log.d("Time", "Game time is up");
        }

        Log.d("Pos", "X:" + getPlayer().getX() + " Y:" + getPlayer().getY());
        this.scoreDisplay.setTextString(
                "Score: " + String.valueOf(this.toad.getScore()) +
                " Coins: " + String.valueOf(this.toad.getCoins())+
                " Bananas: " + String.valueOf(this.toad.getBananas()) +
                " Time: " + getTime()
        );
    }

    private int getTime() {
        return (int)( ((60*ToadParcour.difficulty) - (System.currentTimeMillis()-startTime)/1000) );
    }

    /**
     * creates the view for the player
     * @param player
     * @param zoom
     */
    private void createViewPort(Toad player, float zoom){
        // Switch it on
        Viewport.useViewport = true;
        setBackground("cloudbackground", true);
        // Zoom in, 2x
        setZoomFactor(zoom);
        // Make viewport follow the Vis
        setPlayer(player);
        // Vis will be center screen
        setPlayerPositionOnScreen(Viewport.PLAYER_CENTER, Viewport.PLAYER_CENTER);
        // Determines how quickly viewport moves (see API for details)
        setPlayerPositionTolerance(0, .3);
    }

    public boolean isPlayerGameOver() {
        return playerGameOver;
    }

    public void setPlayerGameOver(boolean playerGameOver) {
        this.playerGameOver = playerGameOver;
        Log.d("Game", "Player is game over");
    }

    public boolean isPlayerOnEndPoint() {
        return isPlayerOnEndPoint;
    }

    public void setPlayerOnEndPoint(boolean isPlayerOnEndPoint) {
        this.isPlayerOnEndPoint = isPlayerOnEndPoint;
    }

    /**
     * Checks if the game time is over or not
     * @return boolean
     */
    public boolean isGameTimeUp() {
        if( System.currentTimeMillis() - this.startTime >= (1000*60) * ToadParcour.difficulty) {
            return true;
        }
        return false;
    }

