    @Override
    public void update() {
        super.update();

        Log.d("Pos", "X:" + getPlayer().getX() + "Y:" + getPlayer().getY());
        this.scoreDisplay.setTextString(
                "Score: " + String.valueOf(this.toad.getScore()) +
                " Coins: " + String.valueOf(this.toad.getCoins())+
                "Bananas: " + String.valueOf(this.toad.getBananas()));
    }


    private void createViewPort(Toad player, float zoom){
        // Switch it on
        Viewport.useViewport = true;
        setBackground("cloudbackground", true);
        // Zoom in, 2x
        setZoomFactor(zoom);
        // Make viewport follow the Vis
        setPlayer(player);
        // Vis will be center screen
        setPlayerPositionOnScreen(Viewport.PLAYER_CENTER, Viewport.PLAYER_CENTER);
        // Determines how quickly viewport moves (see API for details)
        setPlayerPositionTolerance(0.8, 0.5);
    }
